INSERT INTO `chii_episodes` VALUES (7027,253,2,0,0,'野良犬のストラット Stray Dog Strut','野狗阔步',0,'00:22:17','1998-04-03','a:2:{i:0;s:40:\"http://www.tudou.com/v/Q31p9pbfa8A/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765568&amp;yid=XNTEwNDc1OTU2&amp;external=bangumi\";}',38,0,'脚本：横手美智子  絵コンテ：渡辺信一郎  演出：佐藤育郎  作画監督：竹内浩志\n\n賞金首は格闘家の連続ペット窃盗犯。なぜかスパイクたちだけでなく、動物研究所もその男の行方を追っていた……。一匹の犬を巡ってチャイナタウンで巻き起こる大騒動！\n\n第1話から一転、コミカルタッチで描かれたビバップ号の愛犬となるアインの登場秘話。音楽の使われ方も素晴らしく、スパイクのブルース・リーに関するウンチクも冴える！\n\n注：标题取自 Stray Cats 的单曲 Stray Cat Strut 。',1245507149,1629024287,1,0);
INSERT INTO `chii_episodes` VALUES (7028,253,3,0,0,'ホンキィ・トンク・ウィメン Honky Tonk Women','酒馆女郎',0,'00:24:42','1998-04-10','a:2:{i:0;s:40:\"http://www.tudou.com/v/bm3yKoPYCOg/v.swf\";i:1;s:39:\"http://www.bilibili.tv/video/av488321/#\";}',42,0,'脚本：山口亮太/信本敬子  絵コンテ：赤根和樹  演出：森邦宏  作画監督：本橋秀之  メカ作画監督：後藤雅巳\n\n一攫千金を狙ってカジノにやって来たスパイクとジェット。人違いから裏取引に巻き込まれたスパイクたちは、手にした一枚のチップをネタにカジノのオーナー、ゴードンから大金をせしめようとするが……\n\n女イカサマ師・フェイの初登場話。愛機レッドテイルを自在に操り、転んでもタダでは起きないフェイの胡散臭い魅力が爆発のエピソードです！\n\n注：标题取自 The Rolling Stones 的同名歌曲。',1245507198,1637555376,1,0);
INSERT INTO `chii_episodes` VALUES (7029,253,4,0,0,'ゲイトウェイ・シャッフル Gateway Shuffle','网关曳步舞',0,'00:24:41','1998-11-13','a:2:{i:0;s:40:\"http://www.tudou.com/v/OUB9XiOHnwM/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765571&amp;yid=XNTEwNDc1OTYw&amp;external=bangumi\";}',43,0,'脚本：村井さだゆき  絵コンテ：武井良幸  演出：武井良幸  作画監督：しんぼたくろう  メカ作画監督：後藤雅巳\n\n過激な環境保護団体「スペース・ウォリアーズ」の女リーダー、トゥインクルを捕えたスパイクだが、換金しようとした矢先、彼女の賞金が突然取り消されてしまった。渋々トゥインクルを解放するスパイク。だが、トゥインクルは報復のため位相差空間内からウイルスミサイルを発射するのだった……\n\n迫り来るミサイルとスパイクの競走は手に汗握る！\n\n注：曳步舞（Shuffle），由于发源地是澳大利亚墨尔本，故也被叫做墨尔本曳步舞（MelbourneShuffle），早期为了区分 Shuffle 这个词的多元性才加了地区的前缀，目前大多直接用 Shuffle 。曳步舞动作简洁、快速有力、节奏感强，是一种自由度很高、个性十足的舞步。结合本集内容将标题选择了这个中译。',1245507226,1632746318,1,0);
INSERT INTO `chii_episodes` VALUES (7030,253,5,0,0,'堕天使たちのバラッド Ballad Of Fallen Angels','堕天使们的抒情曲',0,'00:24:01','1998-11-20','a:2:{i:0;s:40:\"http://www.tudou.com/v/tHm4Q56CrjI/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765572&amp;yid=XNTEwNDc1OTY4&amp;external=bangumi\";}',91,0,'脚本：横手美智子  絵コンテ：渡辺信一郎  演出：渡辺哲哉  作画監督：川元利浩  メカ作画監督：後藤雅巳\n\nスパイクがレッド・ドラゴンの構成員だった頃の恩人、マオに高額の賞金がかけられた。だがそれは、スパイク抹殺のための罠だった……。マオを殺し、罠を仕掛けたのはかつての相棒、ビシャス。今、二人の対決が幕を開ける！\n\nスパイク生涯の宿敵ビシャスが初登場。オペラとハードアクションが絶妙にマッチしたクールでスタイリッシュな一本だ！',1245507250,1636434213,1,0);
INSERT INTO `chii_episodes` VALUES (7031,253,6,0,0,'恶魔を怜れむ歌 Sympathy For The Devil','给恶魔的哀怜之歌',0,'00:24:42','1998-11-27','a:2:{i:0;s:40:\"http://www.tudou.com/v/NdJexUdDqMI/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765573&amp;yid=XNTEwNDc1OTcy&amp;external=bangumi\";}',54,0,'脚本：信本敬子  絵コンテ：岡村天斎  演出：佐藤育郎  作画監督：竹内浩志  メカ作画監督：後藤雅巳\n\nスパイクたちが追う賞金首のジラフが何者かに殺された。調査を開始したスパイクは、ジラフが探していたゼブラという車椅子の男に辿り着く。だが、そこで口を開いたのはゼブラに付き添っていた少年のウェンだった……少年に隠された秘密とは？\n\nスパイクが天才少年ブルースハーピストの謎に迫る！オカルトチックなSF感溢れる奇妙な物語。\n\n注：标题取自 The Rolling Stones 的同名歌曲。',1245507269,1636558342,1,0);
INSERT INTO `chii_episodes` VALUES (7032,253,7,0,0,'ヘヴィ・メタル・クイーン Heavy Metal Queen','重金属皇后',0,'00:24:42','1998-04-17','a:2:{i:0;s:40:\"http://www.tudou.com/v/Z1edXjWyL14/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765574&amp;yid=XNTEwNDc1OTc2&amp;external=bangumi\";}',49,0,'脚本：横手美智子  絵コンテ：岡村天斎  演出：森邦宏  作画監督：本橋秀之  メカ作画監督：後藤雅巳\n\n爆発物のスペシャリストで賞金首のデッカーを追っている最中、スパイクは女トラッカーのVTと出会う。一方、賞金を一人占めしようと別行動でデッカーを探していたフェイは、一度は発見するが取り逃がしてしまう。それを知ったVTは、トラック仲間からの情報でデッカーが乗った機体の追跡を開始するが……\n\n全編に流れるヘヴィ・メタルが痛快！',1245507294,1631030321,1,0);
INSERT INTO `chii_episodes` VALUES (7033,253,8,0,0,'ワルツ・フォー・ヴィーナス Waltz For Venus','金星华尔兹',0,'00:24:35','1998-04-24','a:2:{i:0;s:40:\"http://www.tudou.com/v/4plGLX4Ba08/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765575&amp;yid=XNTEwNDc1OTky&amp;external=bangumi\";}',45,0,'脚本：横手美智子  絵コンテ：武井良幸  演出：武井良幸  作画監督：しんぼたくろう  メカ作画監督：後藤雅巳\n\n金星病で視力を失った妹ステラのため、自分の所属するピカロ一味から、金星病の薬草「グレイアッシュ」を持ち逃げした青年ロコ。その逃走中、スパイクと出会ったロコは、薬草をスパイクに託し、姿を消してしまう。数日後、ロコに指定された場所に向かったスパイクを待っていたのは……\n\n最後に流れるオルゴールの音色が静かに哀しみを強調する。',1245507313,1639368639,1,0);
INSERT INTO `chii_episodes` VALUES (7034,253,9,0,0,'ジャミング・ウィズ・エドワードJamming With Edward','与艾德华竞飙',0,'00:24:33','1998-05-01','a:2:{i:0;s:40:\"http://www.tudou.com/v/gJPNk8rxR_s/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765576&amp;yid=XNTEwNDc1OTg4&amp;external=bangumi\";}',60,0,'脚本：佐藤大  絵コンテ：渡辺信一郎  演出：佐藤育郎  作画監督：小森高博  メカ作画監督：後藤雅巳\n\n地表に人工衛星を使って落書きをしたハッカーを追って地球に来たスパイクたち。それを知ったエドは、兼ねてから興味を持っていたスパイクたちの手助けをしようと犯人を追うが……\n\n天才ハッカー、エドの初登場話。地表に絵を描いた衛星をハッキングしたり、ビバップ号をラジコン飛行機のように操ったりと、彼女の悪戯心が遺憾なく発揮されている！\n\n注：标题取自 The Rolling Stones 的三位成员与 Nicky Hopkins 、 Ry Cooder 在1972年发行的专辑名。',1245507350,1629129420,1,0);
INSERT INTO `chii_episodes` VALUES (7035,253,10,0,0,'ガニメデ慕情 Ganymede Elegy','木卫三挽歌',0,'00:24:41','1998-05-08','a:2:{i:0;s:40:\"http://www.tudou.com/v/0EuWbemfv28/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765577&amp;yid=XNTEwNDc1OTg0&amp;external=bangumi\";}',68,0,'脚本：稲荷昭彦  絵コンテ：山口祐司  演出：山田弘和  作画監督：逢坂浩司  メカ作画監督：後藤雅巳\n\nある日突然、自分のもとから去って行った、かつての恋人アリサのことを昔馴染みから聞かされたジェットは、一人で故郷ガニメデに向かう。ガニメデに到着し、アリサと再会したジェットだったが、アリサには新たな恋人リントがいた。だが、リントは賞金首だったのだ……\n\n様々な音楽がモチーフとなる「ビバップ」だが、今回はズバリ“演歌”です！',1245507371,1636884301,1,0);
INSERT INTO `chii_episodes` VALUES (7036,253,11,0,0,'闇夜のヘヴィ・ロック Toys In The Attic','阁楼上的玩具',0,'00:24:41','1998-05-15','a:2:{i:0;s:40:\"http://www.tudou.com/v/FHUgg8yVwNY/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765578&amp;yid=XNTEwNDc1OTk2&amp;external=bangumi\";}',82,0,'脚本：横手美智子  絵コンテ：森邦宏  演出：森邦宏  作画監督：しんぼたくろう  メカ作画監督：後藤雅巳\n\nビバップ号の船内でジェットが何かに首筋を刺され、そのまま気絶してしまった。真相の究明に乗り出すスパイクだが、病名はおろか刺した生物の見当すらつかない。そうこうしている内に、続いてフェイ、アインも襲われ、床に伏せてしまうのだった……\n\n映画「エイリアン」シリーズを彷彿させるビバップ号を舞台にした密室サスペンス・パロディー！\n\n注：标题取自美国摇滚乐队 Aerosmith 的同名专辑。',1245507402,1639329856,1,0);
INSERT INTO `chii_episodes` VALUES (7037,253,12,0,0,'ジュピター・ジャズ（前编） Jupiter Jazz (PART 1)','木星爵士乐（前篇）',0,'00:24:41','1998-05-22','a:2:{i:0;s:40:\"http://www.tudou.com/v/7x2WOUhOV9k/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765579&amp;yid=XNTEwNDc2MDAw&amp;external=bangumi\";}',39,0,'脚本：信本敬子  絵コンテ：岡村天斎  演出：武井良幸  作画監督：川元利浩  メカ作画監督：後藤雅巳\n\n目薬ドラッグ「レッド・アイ」の取引に、木星の衛星カリストへ向かうビシャス。その頃、ビバップ号の金庫の中身を持ち逃げしたフェイを追っていたスパイクたちは、ジュリアという名前にぶち当たる。女の名に顔色を変えたスパイクは、ジェットの制止を振り切りカリストに向かう。その頃、フェイはグレンと名乗る不思議な青年に出会っていた……',1245507419,1637122117,1,0);
INSERT INTO `chii_episodes` VALUES (7038,253,13,0,0,'ジュピター・ジャズ（后编） Jupiter Jazz (PART 2)','木星爵士乐（后篇）',0,'00:24:41','1998-05-29','a:2:{i:0;s:40:\"http://www.tudou.com/v/-Ddsjlf5X5E/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765581&amp;yid=XNTEwNDc2MDA0&amp;external=bangumi\";}',53,0,'脚本：信本敬子  絵コンテ：岡村天斎  演出：佐藤育郎  作画監督：小森高博  メカ作画監督：後藤雅巳\n\nビシャスと再会したスパイクは、かつての弟分リンの銃撃の前に倒れ、グレンはフェイにビシャスとの壮絶な過去を告げる。そして、グレンはビシャスに復讐するため罠を仕掛ける。そこに復活したスパイクも加わり、ビシャス、グレン、スパイクによる三つ巴の空中戦が始まった！\n\n名曲「SPACE LION」が流れるエンディングが渋くて超カッコ良いぞ！',1245507441,1637122264,1,0);
INSERT INTO `chii_episodes` VALUES (7039,253,14,0,0,'ボヘミアン・ラプソディ Bohemian Rhapsody','波西米亚狂想曲',0,'00:24:41','1998-06-05','a:2:{i:0;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765580&amp;yid=XNTEwNDc2MDA4&amp;external=bangumi\";i:1;s:47:\"http://www.tudou.com/programs/view/yxOKL03hgaU/\";}',43,0,'脚本：佐藤大  絵コンテ：都留稔幸  演出：山田弘和  作画監督：しんぼたくろう  メカ作画監督：後藤雅巳\n\n次々と起こる位相差空間ゲートのハッキング事件。スパイクたちもその実行犯は捕えるものの、彼らを操る首謀者の姿が見えてこない。唯一の手がかりは実行犯が持っていたチェスの駒のみであった。だが、その駒の仕掛けに気付いたエドは、チェスのネット対戦を始め、黒幕の“チェスマスター・ヘックス”の存在を突き止める。果たして、その正体とは？\n\n注：标题取自 Queen 的同名歌曲。',1245507458,1637209324,1,0);
INSERT INTO `chii_episodes` VALUES (7040,253,15,0,0,'マイ・ファニー・ヴァレンタイン My Funny Valentine','我可爱的情人',0,'00:24:41','1998-06-12','a:1:{i:0;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765582&amp;yid=XNTEwNDc2MDEy&amp;external=bangumi\";}',52,0,'脚本：信本敬子  絵コンテ：岡村天斎  演出：森邦宏  作画監督：逢坂浩司  メカ作画監督：後藤雅巳\n\nある日、フェイはアインに自分の過去のことを呟く。三年前にコールドスリープから目覚めたフェイ。だが、フェイはコールドスリープ以前の記憶を失っていた。その上、手術代など合わせて三億ウーロンの借金も背負ってしまう。そんなフェイを支えていたのが担当の弁護士ウィットニーだった……\n\nフェイの秘められた過去が垣間見られるエピソード！\n\n注：标题取自 Chet Baker 的同名歌曲。',1245507529,1637897682,1,0);
INSERT INTO `chii_episodes` VALUES (7041,253,16,0,0,'ブラック・ドッグ・セレナーデ Black Dog Serenade','黑犬小夜曲',0,'00:24:41','1999-02-12','a:2:{i:0;s:40:\"http://www.tudou.com/v/HpClY0AFJb8/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765583&amp;yid=XNTEwNDc1NzEy&amp;external=bangumi\";}',37,0,'脚本：横手美智子  絵コンテ：山内重保  演出：佐藤育郎  作画監督：竹内浩志  メカ作画監督：後藤雅巳\n\n冥王星へ向かう囚人輸送宇宙船の内部で、囚人たちによる反乱が発生した。その中には、昔、ジェットの左腕を奪った殺し屋ウダイがいた。かつての同僚ファドからその情報を聞いたジェットは、ファドと共に包囲網を突破して行方不明となった宇宙船の追跡を開始するが……\n\nジェットが“ブラック・ドッグ”と呼ばれていた警官時代の過去が描かれる。',1245507559,1638792827,1,0);
INSERT INTO `chii_episodes` VALUES (7042,253,17,0,0,'マッシュルーム・サンバ Mushroom Samba','蘑菇桑巴',0,'00:24:18','1999-02-19','a:2:{i:0;s:40:\"http://www.tudou.com/v/zrPgavNwQs0/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765584&amp;yid=XNTEwNDc2MjQ4&amp;external=bangumi\";}',59,0,'脚本：横手美智子/渡辺信一郎  絵コンテ：渡辺信一郎  演出：森邦宏  作画監督：しんぼたくろう/中田栄治  メカ作画監督：後藤雅巳\n\n一文無しで、宇宙を漂流していたビバップ号は当て逃げに遭い、木星の衛星イオに不時着してしまう。近くの町に食料を調達に出かけたエドとアインは、一人の不思議な老人に出会う。その老人からキノコを貰ったエドは、腹を減らしたビバップ号の面々にお裾分け。すると、めくるめくトリップの世界が彼らを待っていた……\n\nエドとアインの魅力全開！',1245507613,1632281102,1,0);
INSERT INTO `chii_episodes` VALUES (7043,253,18,0,0,'スピーク・ライク・ア・チャイルド Speak Like a Child','童言童语',0,'00:24:42','1998-06-19','a:2:{i:0;s:40:\"http://www.tudou.com/v/pN8kDInaWX8/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765586&amp;yid=XNTEwNDc1NzE2&amp;external=bangumi\";}',116,0,'脚本：稲荷昭彦/河森正治/吉永亜矢  絵コンテ：佐藤順一  演出：武井良幸  作画監督：菅野宏紀  メカ作画監督：後藤雅巳\n\nビバップ号にある日、フェイ宛の小包が届く。借金取りからの物だと勘違いしたフェイは、中身も見ないで逃走。スパイクが残された小包を開けると、一本のベータテープが出てきた。その筋のマニアに売ろうとして諦めたスパイクとジェットがその中身を再生すると、そこには少女時代のフェイの映像が映っていた……。「フレー、フレー、あたし！」。',1245507643,1638538575,1,0);
INSERT INTO `chii_episodes` VALUES (7044,253,19,0,0,'ワイルド・ホーセス Wild Horses','野马',0,'00:24:29','1999-03-05','a:2:{i:0;s:40:\"http://www.tudou.com/v/EeGfXNG-o5Y/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765595&amp;yid=XNTEwNDc1NzIw&amp;external=bangumi\";}',37,0,'脚本：稲荷昭彦  絵コンテ：飯田馬之介  演出：山田弘和  作画監督：逢坂浩司  メカ作画監督：後藤雅巳\n\nスパイクは久し振りに愛機ソードフィッシュIIをオーバーホールしてもらうため、凄腕メカニック、ドゥーハンのいる地球を訪れていた。修復が終わったソードフィッシュIIで早速、宇宙海賊スターシップ・パイレーツを追うスパイクだが、突如制御力を失い地球へと落下し始める！　スパイクの運命は？\n\nメカデザイナー・山根公利の趣味が炸裂した一本。',1245507687,1639366528,1,0);
INSERT INTO `chii_episodes` VALUES (7045,253,20,0,0,'道化師の鎮魂歌 Pierrot Le Fou','小丑的安魂曲',0,'00:24:40','1999-03-12','a:3:{i:0;s:47:\"http://v.youku.com/v_show/id_XMjg2NDk1MjEy.html\";i:1;s:40:\"http://www.tudou.com/v/9AypItnwnYY/v.swf\";i:2;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130765585&amp;yid=XNTEwNDc2MzA4&amp;external=bangumi\";}',103,0,'脚本：村井さだゆき  絵コンテ：武井良幸  演出：武井良幸  作画監督：小森高博  メカ作画監督：後藤雅巳\n\nある夜、スパイクは警察の高官が暗殺されるのを目撃した。暗殺者は、マッドピエロこと東風（トンプウ）。その姿を見て生き延びた者はいないという伝説の殺し屋だった。目撃したことで東風の襲撃を受けるスパイク。その場は何とか逃げ延びたものの、東風はどこまでも追って来るのだった……。\n\n本気になったスパイクのキレのあるアクションが最高！',1245507706,1639728343,1,0);
INSERT INTO `chii_episodes` VALUES (7046,253,21,0,0,'ブギ・ウギ・フンシェイ Boogie Woogie Feng Shui','布吉伍吉风水',0,'00:24:40','1999-03-19','a:2:{i:0;s:40:\"http://www.tudou.com/v/aMjXwypBI2s/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130766707&amp;yid=XNTEwNDc1NzI0&amp;external=bangumi\";}',44,0,'脚本：村井さだゆき/渡辺信一郎  絵コンテ：潮乱太  演出：佐藤育郎  作画監督：竹内浩志  メカ作画監督：後藤雅巳\n\n古い友人のパオよりジェットに届いた一通のメール。文面には「案山より聖獣を見いだせ。我、四神相応に有り」という謎の言葉。疑問に思い、パオを探していたジェットは、パオが位相差空間内に閉じこめられていることを知る。そしてパオの孫娘メイファを付け狙う刺客の影。それは秘密のエネルギーを秘めた「太陽石」を巡る戦いの始まりだった……。\n\n注：ブギ・ウギ（Boogie Woogie）低音连奏爵士乐，是爵士乐中的一种钢琴奏法。',1245507728,1639295016,1,0);
INSERT INTO `chii_episodes` VALUES (7047,253,22,0,0,'カウボーイ・ファンク Cowboy Funk','牛仔放克',0,'00:24:41','1999-03-26','a:2:{i:0;s:40:\"http://www.tudou.com/v/tOucNYXO5WE/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130766702&amp;yid=XNTEwNDc1NzI4&amp;external=bangumi\";}',80,0,'脚本：信本敬子  絵コンテ：岡村天斎  演出：森邦宏  作画監督：川元利浩  メカ作画監督：後藤雅巳\n\nヌイグルミに爆弾を仕掛けて世を騒がす爆弾魔テディ・ボマーを追うスパイクたちは、後一歩のところでスパイクを犯人と勘違いしたカウボーイ姿の賞金稼ぎ、アンディの妨害を受ける。それがスパイクの長い長いホンキィトンクな日々の始まりだった……。\n\nシリーズ中屈指のキャラ、アンディ登場。ある意味一番「ビバップ」らしい最高に笑えるエピソード！',1245507773,1639543480,1,0);
INSERT INTO `chii_episodes` VALUES (7048,253,23,0,0,'ブレイン・スクラッチ Brain Scratch','大脑划痕',0,'00:24:40','1999-04-02','a:3:{i:0;s:40:\"http://www.tudou.com/v/cmGeW2KONLo/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130766703&amp;yid=XNTEwNDc1NzMy&amp;external=bangumi\";i:2;s:59:\"http://www.tudou.com/albumplay/7N-t2tvbwh0/kDQH5MLkfsI.html\";}',67,0,'脚本：佐藤大  絵コンテ：武井良幸  演出：武井良幸  作画監督：竹内浩志  メカ作画監督：後藤雅巳\n\nネット上に存在する謎の宗教団体・電子移民財団スクラッチ。その信者が次々に行方不明になり、教祖のロンデスに多額の賞金がかけられた。早速、抜け駆けするフェイ。しかし、ロンデスの正体は知れず、スクラッチの本部で賞金稼ぎたちの屍を目の当たりにする。フェイからの連絡にスパイクたちは渋々調査を始めるが……。\n\n退廃的なムードが全編を覆う。',1245507793,1639298677,1,0);
INSERT INTO `chii_episodes` VALUES (7049,253,24,0,0,'ハード・ラック・ウーマン Hard Luck Woman','不走运的女人',0,'00:24:37','1999-04-09','a:2:{i:0;s:40:\"http://www.tudou.com/v/Vmt41OXnYnA/v.swf\";i:1;s:88:\"http://dp.tudou.com/nplayer.swf?iid=130766705&amp;yid=XNTEwNDc1NzM2&amp;external=bangumi\";}',128,0,'脚本：横手美智子  絵コンテ：岡村天斎  演出：山田弘和  作画監督：菅野宏紀  メカ作画監督：後藤雅巳\n\nかつて自分に宛てて送ったビデオレターが告げる、なくしてしまった過去。思い出せぬ記憶。その中にマーライオンの像が映っていることに気付いたフェイは、その像のある場所を知っているらしいエドと共に地球に向かった。立ち寄った修道院で明らかになるエドの父の消息とマーライオンの場所。そして、フェイは自分を知る老女に出会うのだった……。',1245507816,1639669434,1,0);
