INSERT INTO `chii_persons` VALUES (7,'熊谷哲矢',1,'{{Infobox Crt\n|简体中文名= 熊谷哲矢\n|别名={\n[第二中文名|熊谷哲也]\n[英文名|]\n[日文名|熊谷哲矢]\n[纯假名|]\n[罗马字|Kumagai Tetsuya]\n[昵称|]\n}\n|性别= 男\n|生日=\n|血型=\n|身高=\n|体重=\n|BWH=\n|引用来源={\n}\n|妻= 佐山聖子\n}}',1,0,0,0,0,0,0,'岩手県出身、妻は演出家の佐山聖子。\nスタジオジュニオ、ハルフィルムメーカーを経て現在はフリー。\n「熊谷哲也」名義は、近年の作品のものは別人の名前の誤植による可能性もあるので編集の際は注意。','','',1,4,0,1595403223,0,4536,0,0,0);
