INSERT INTO `chii_subject_revisions` VALUES (48260,1,1,1,13974,1323056833,'第一次的親密接觸','','{{Infobox animanga/Novel\n|中文名=\n|别名={\n}\n|出版社= 知识出版社\n|价格=\n|插图=\n|连载杂志=\n|发售日= 1999-11\n|页数=\n|话数=\n|ISBN= 9787501524044\n|其他=\n|作者= 蔡智恒\n}}','','　　風靡華人世界的網戀小說經典，暢銷紀錄超過百萬冊！\n　　有最初的《第一次的親密接觸》，才有今日網路文學。\n　　蔡智恆的最新作品《暖暖》，提升你的戀愛幸福溫度。\n　　紀念版收錄蔡智恆醞釀十年，最溫柔的純愛萬語宣言。\n　　PH值小於7的微酸心情，讓十年後的痞子蔡告訴你。\n　　一場最美麗的網路解逅，當痞子蔡遇見輕舞飛揚……\n　　蔡智恆最真實的情感原點\n　　痞子蔡與輕舞飛揚一起記錄的甜蜜日子\n　　一則發生在成大校園的純愛故事，網路上一再被轉載的熱門小說。','',0,'',0);
